import React, { useState } from 'react';
import './style.css';
import post1 from './Imgs/post1.png'
import jetiLogo from '../Stories/Img/jetLogo.jpg';
import { AiOutlineHeart , AiFillHeart} from 'react-icons/ai';
import { Sidebar } from '../Sidebar'
import { Stories } from '../Stories'

export function Home() {

    const [likes, setLikes] = useState(100)
    const [liked, setLikedActive] = useState(false)
    const [filled, setFilled] = useState(false)

    function like() {
        if (liked) {
            setLikedActive(false)
            setLikes(likes-1)
        }else{
            setLikedActive(true)
            setLikes(likes+1)
            setFilled(true)
            filled = document.getElementsByClassName('heart-btn')
            filled.target.classList.toggle('.btn-filled')
        }
    }

    return(
        <div className="home-box">
            <Sidebar/>
            <Stories/>
        <div className="feed">
            <ul className="posts">
                <li className="post1">
                    <img className="post-profile-pic" src={jetiLogo} align="left" alt="Profile1" />
                    <p className="post-profile-name">jetimobi</p>
                    <img className= "post" src={post1}  alt="Post1" />
                    <p>Curtido por: <strong>{likes}</strong> pessoas</p>
                    <AiOutlineHeart className='heart-btn' onClick={like}/>

                </li>

                <li className="post2">
                    <img className="post-profile-pic" src={jetiLogo} align="left" alt="Profile1" />
                    <p className="post-profile-name">jetimobi</p>
                    <img className= "post" src={post1}  alt="Post1" />
                </li>

                <li className="post3">
                    <img className="post-profile-pic" src={jetiLogo} align="left" alt="Profile1" />
                    <p className="post-profile-name">jetimobi</p>
                    <img className= "post" src={post1}  alt="Post1" />
                </li>
            </ul>
        </div>
        </div>
    )
}
